mapper.select:
SELECT B.*, P.* FROM BABY B, PARENT P
WHERE B.PARENT_NO = P.PARENT_NO

	AND BABY_NO = #{baby_no}

SELECT B.DIS_NAME 
FROM DIAGNOSIS A , DISEASE B

	(A.DIS_CODE=B.DIS_CODE) AND RESV_NO = #{resv_no}

mapper.update:
UPDATE RESERVATION
SET RESV_STATUS = #{resv_status}
WHERE RESV_NO = #{resv_no}

mapper.select:
		SELECT R.*, C.*, E.*, H.*
		FROM CHECKUP C, RESERVATION R, EMPLOYEE E, HOSPITAL H
		WHERE R.CHK_NO = C.CHK_NO
   		AND E.EMP_NO = #{emp_no}
		AND RESV_NO = #{resv_no}

mapper.insert:
SELECT NVL(MAX(HIST_NO),0)+1 FROM CHECKHIST
INSERT INTO CHECKHIST(HIST_NO, CHK_NO, HIST_STATE, RESV_NO, HIST_DATE
) VALUES(#{hist_no}, #{chk_no}, #{hist_state}, #{resv_no}, SYSDATE
)

mapper.select:
SELECT * FROM IMAGES


		RESV_NO = #{resv_no}


ORDER BY IMG_DATE
