mapper.select:
SELECT R.RESV_NO, TO_CHAR(R.RESV_DATE, 'YYYY-MM-DD') AS RESV_DATE, R.RESV_DETAIL, R.BABY_NO, D.DIAG_PHOTO FROM RESERVATION R LEFT JOIN DIAGNOSIS D ON R.RESV_NO = D.RESV_NO
BABY_NO = ${baby_no}
ORDER BY RESV_NO

SELECT B.*, P.* FROM BABY B, PARENT P
WHERE B.PARENT_NO = P.PARENT_NO

	AND BABY_NO = ${baby_no}
